mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT NUM, WRITER, CAPTION, IMAGEPATH, REGDATE FROM BOARD_GALLERY
ORDER BY num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

mapper.update:
UPDATE BOARD_GALLERY
SET CAPTION   = #{caption},
    IMAGEPATH = #{imagePath}
WHERE num = #{num}
